package javaQuestions;

public class swap2number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=20;
		int c;
		
		c=a;
		a=b;
		b=c;
		
		System.out.println(a + "," +b);
		
		int x=50;
		int y=100;
		
		x=x+y;
		y=x-y;
		x=x-y;
		
		System.out.println(x + "," +y);
	}

}
